import React, { useState } from "react";
import "../pwetty/Heropage.css"; 
import bioimg1 from "../assets/hero_imgs/hero-bio.png";
import bioimg2 from "../assets/hero_imgs/hero-bio2.png";
import designimg1 from "../assets/hero_imgs/hero-design.png";
import designimg2 from "../assets/hero_imgs/design2.png";

function Heropage() {
    const [isMobile, setIsMobile] = useState(false);

    // Handle resizing for responsiveness
    const handleResize = () => {
        if (window.innerWidth <= 768) {
            setIsMobile(true);
        } else {
            setIsMobile(false);
        }
    };

    React.useEffect(() => {
        handleResize();
        window.addEventListener("resize", handleResize);

        return () => window.removeEventListener("resize", handleResize);
    }, []);

    return (
        <main className="heropage-main">
            <div className={`heropage-left ${isMobile ? "mobile" : ""}`}>
                <h1>bioinformatics work ⤦</h1>
                <div className="bio-img-wrapper">
                    <img className="bio-img-normal" src={bioimg1} alt="Bioinformatics" />
                    <img className="bio-img-hover" src={bioimg2} alt="Bioinformatics Hover" />
                </div>
                <h3>biomedical computing honours</h3>
                <p> see the academic works and research and experience <br />as an undergraduate bio/cs student. </p>
            </div>
            <div className={`heropage-right ${isMobile ? "mobile" : ""}`}>
                <h1>design & creatives ˎˊ˗</h1>
                <div className="design-img-wrapper">
                    <img className="design-img-normal" src={designimg1} alt="Design" />
                    <img className="design-img-hover" src={designimg2} alt="Design Hover" />
                </div>
                <h3>art & design is my fun.</h3>
                <p> see my design portfolio, mini creatives & art things i’ve made for fun. </p>
            </div>
        </main>
    );
}

export default Heropage;
